		//番茄翻译
		ACCOUNT_TOMATO = new AlipayAccountBean();
		ACCOUNT_TOMATO.setAppid("2018031302366026");
		ACCOUNT_TOMATO.setAppPrivateKey("");
		ACCOUNT_TOMATO.setAlipayPublicKey("");
